discoverGrade.query:
query discoverGrade($gradeID: ID!) {
  discoverGrade(gradeID: $gradeID) {
    id
    name
    type
    season {
      id
      name
      competition {
        id
        name
        organisation {
          ...OrganisationDetails
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  tenantConfiguration {
    label
    statistics {
      enabled
      __typename
    }
    ...TenantContactRolesConfiguration
    gradeTypes {
      value
      isAllPlay
      features {
        hasRounds
        hasSeasonStats
        hasCareerStats
        hasGameStats
        hasFixture
        hasLadder
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment OrganisationDetails on DiscoverOrganisation {
  id
  type
  name
  email
  contactNumber
  websiteUrl
  address {
    id
    line1
    suburb
    postcode
    state
    country
    __typename
  }
  logo {
    sizes {
      url
      dimensions {
        width
        height
        __typename
      }
      __typename
    }
    __typename
  }
  contacts {
    id
    firstName
    lastName
    position
    email
    phone
    __typename
  }
  shopVisible
  __typename
}

fragment TenantContactRolesConfiguration on TenantConfiguration {
  contactRoles {
    name
    value
    __typename
  }
  __typename
}

